mapper.select:
select
        id as id,
        uName as uName,
        uPwd as uPwd,
        uPhone as uPhone,
        uSex as uSex,
        uIdentity as uIdentity,
        uRole as uRole
from user

            
                and id=#{id}
            
            
                and uName=#{uName}
            
            
                and uPwd=#{uPwd}
            
            
                and uPhone=#{uPhone}
            
            
                and uSex=#{uSex}
            
            
                and uIdentity=#{uIdentity}
            
            
                and uRole=#{uRole}
            


    limit #{start},#{size}

select count(*) from user

        
            and id=#{id}
        
        
            and uName=#{uName}
        
        
            and uPwd=#{uPwd}
        
        
            and uPhone=#{uPhone}
        
        
            and uSex=#{uSex}
        
        
            and uIdentity=#{uIdentity}
        
        
            and uRole=#{uRole}
