mapper.select:
select
        id as id,
        hName as hName,
        sId as sId,
        hGrage as hGrage,
        hContent as hContent
from hotel

            
                and id=#{id}
            
            
                and hName=#{hName}
            
            
                and hPirce=#{hPirce}
            
            
                and sId=#{sId}
            
            
                and hGrage=#{hGrage}
            
            
                and hContent=#{hContent}
            


    limit #{start},#{size}

select count(*) from hotel

        
            and id=#{id}
        
        
            and hName=#{hName}
        
        
            and hPirce=#{hPirce}
        
        
            and sId=#{sId}
        
        
            and hGrage=#{hGrage}
        
        
            and hContent=#{hContent}

SELECT p.id,p.pRelativePath FROM picture p,hotel h WHERE p.pTypeId=h.id AND p.pType=3 AND 
	p.pTypeId in 
	
		#{pTypeId}
