mapper.select:
select
        id as id,
        sId as sId,
        lName as lName,
        lContent as lContent
from line

            
                and id=#{id}
            
            
                and sId=#{sId}
            
            
                and lName=#{lName}
            
            
                and lContent=#{lContent}
            

 order by createDate desc

    limit #{start},#{size}

select count(*) from line

        
            and id=#{id}
        
        
            and sId=#{sId}
        
        
            and lName=#{lName}
        
        
            and lContent=#{lContent}
