mapper.select:
select
        id as id,
        sId as sId,
        sPrice as sPrice,
        lId as lId,
        lPrice as lPrice,
        hId as hId,
        hPrice as hPrice,
        oTotalPirce as oTotalPirce,
        oNumber as oNumber,
        oCreateTime as oCreateTime,
        oStatus as oStatus
from orders

            
                and id=#{id}
            
            
                and sId=#{sId}
            
            
                and sPrice=#{sPrice}
            
            
                and lId=#{lId}
            
            
                and lPrice=#{lPrice}
            
            
                and hId=#{hId}
            
            
                and hPrice=#{hPrice}
            
            
                and oTotalPirce=#{oTotalPirce}
            
            
                and oNumber=#{oNumber}
            
            
                and oCreateTime=#{oCreateTime}
            
            
                and oStatus=#{oStatus}
            

 order by createDate desc

    limit #{start},#{size}

select count(*) from orders

        
            and id=#{id}
        
        
            and sId=#{sId}
        
        
            and sPrice=#{sPrice}
        
        
            and lId=#{lId}
        
        
            and lPrice=#{lPrice}
        
        
            and hId=#{hId}
        
        
            and hPrice=#{hPrice}
        
        
            and oTotalPirce=#{oTotalPirce}
        
        
            and oNumber=#{oNumber}
        
        
            and oCreateTime=#{oCreateTime}
        
        
            and oStatus=#{oStatus}
